import {Injectable} from "@angular/core";

export interface ToppingsStateModel {
  toppings: Topping[];
  selectedToppings: Topping[];
  loaded: boolean;
  loading: boolean;
}

@State<ToppingsStateModel>({
  name: 'toppingsState',
  defaults: {
